customCSS:
/* Marble & Glass Design - Background Texture */
html, body {
  background-image: url("data:image/svg+xml,%3Csvg width='6' height='6' viewBox='0 0 6 6' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='%23767676' fill-opacity='0.05' fill-rule='evenodd'%3E%3Cpath d='M5 0h1L0 5v1H5v-1H1z'/%3E%3C/g%3E%3C/svg%3E") !important;
  background-size: auto !important;
  background-position: center !important;
  background-repeat: repeat !important;
}

.sb-component-section, .sb-component-hero-section, .sb-component-featured-items-section, .sb-component-generic-section, .sb-component-carousel-section, .sb-component-featured-posts-section, .sb-component-form-section, .sb-component-image-gallery-section, .section, .page-layout, .page-layout-section, .page-layout-content, .page-layout-container, .page-layout-main, .page-layout-body, .page-layout-header, .page-layout-footer {
  background-color: transparent !important;
  background-image: none !important;
}

/* Card styling */
.featured-item, .form-block, .image-block, .generic-section {
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  background-color: #ffffff;
}

.featured-item:hover {
  transform: translateY(-5px);
  box-shadow: 0 15px 35px rgba(0, 0, 0, 0.08);
}

/* Improved spacing */
.section {
  margin-bottom: 80px;
}

/* Info block styling for special content */
.info-block {
  background-color: #ffffff;
  padding: 30px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);
  position: relative;
  z-index: 2;
}

.info-title {
  font-family: 'Cormorant Garamond', serif;
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 10px;
  color: #0F4C81;
}

.info-text {
  color: #212529;
  font-size: 14px;
  line-height: 1.6;
}
